import { getError, getErrorByCode, isReservedErrorCode } from "./error";
import { INTERNAL_ERROR, SERVER_ERROR } from "./constants";
export function payloadId() {
  var date = Date.now() * Math.pow(10, 3);
  var extra = Math.floor(Math.random() * Math.pow(10, 3));
  return date + extra;
}
export function formatJsonRpcRequest(method, params, id) {
  return {
    id: id || payloadId(),
    jsonrpc: "2.0",
    method: method,
    params: params
  };
}
export function formatJsonRpcResult(id, result) {
  return {
    id: id,
    jsonrpc: "2.0",
    result: result
  };
}
export function formatJsonRpcError(id, error) {
  return {
    id: id,
    jsonrpc: "2.0",
    error: formatErrorMessage(error)
  };
}
export function formatErrorMessage(error) {
  if (typeof error === "undefined") {
    return getError(INTERNAL_ERROR);
  }

  if (typeof error === "string") {
    error = Object.assign(Object.assign({}, getError(SERVER_ERROR)), {
      message: error
    });
  }

  if (isReservedErrorCode(error.code)) {
    error = getErrorByCode(error.code);
  }

  return error;
}
